mapper.select:
SELECT id, seller_id, user_id, order_no, good_snapshot_id, delivery_id, order_status, order_type, pay_type, message, good_name, count, good_id, remark, good_fee, payment_fee, reduce_fee, order_fee, refund_fee, use_point FROM t_order WHERE user_id=#{userId}
AND order_status=#{orderStatus}
AND pay_type=#{payType}
